import React, { useState, useEffect } from 'react';

import Button from '../UI/Button/Button';
import tmdbApi from '../../api/tmdbApi';
import apiConfig from '../../api/apiConfig';

import './Banner.scss';
import { useSelector } from 'react-redux';
import { dataSelector } from '../../store/data';

export default function Banner() {
  const data = useSelector(dataSelector.data);
  console.log(data);
  const [movie, setMovie] = useState([]);
  const [error, setError] = useState(false);
  const [urlBanner, setUrlBanner] = useState(null);
  const imgType = 'backdrop_path';

  const movieBackdrop = data.filter(item => item.backdrop_path);

  const random = Math.floor(Math.random() * movieBackdrop.length - 1);
  const movieRandom = movieBackdrop[random];
  console.log(movieRandom?.backdrop_path);
  return (
    <>
      (
      <div
        className='banner'
        style={{
          backgroundImage: `url(${apiConfig.originalImage(
            movieRandom?.backdrop_path
          )})`,
        }}
      >
        <div className='container'>
          <div className='banner-text'>
            <h2 className='name'>
              {movieRandom?.name || movieRandom?.title || ''}
            </h2>
            <Button className='btn-white mr-1'>Play</Button>
            <Button className='btn-white '>My List</Button>
            <p className='overview mt-1'>{movieRandom?.overview}</p>
          </div>
        </div>
      </div>
      )}
    </>
  );
}
